mapper.insert:
insert into t_textbook_info(book_id,book_short_name,book_full_name)
select db_key,textvalue,all_textvalue from t_sync_mid
where db_key not in(
  select book_id from t_textbook_info
);

mapper.update:
update t_textbook_info a,t_sync_mid b
set a.book_full_name = b.all_textvalue,a.book_short_name= b.textvalue
where a.book_id = b.db_key;

mapper.insert:
insert into t_dictionary_info(dict_flag,dict_value,dict_text,deleted,ext1,ext2)
select #{dictFlag},db_key,textvalue,'0',#{desc},#{type} from t_sync_dict_mid
where type=#{type} and db_key not in(
select dict_value from t_dictionary_info where ext2=#{type}
);

mapper.update:
update t_dictionary_info a,t_sync_dict_mid b
set a.dict_text = b.textvalue
where a.dict_value = b.db_key and a.ext2 = b.type;

mapper.select:
select DISTINCT substring_index(book_id,'-', 1) as bookId,book_short_name as name
from t_textbook_info

  and book_id = #{bookId}


  order by ${orderByClause}
