mapper.select:
select id, cas_type,cas_user_id, username, realname, user_type, gender, birthday, tel_num,status
from t_user_info




  order by ${orderByClause}

select a.id, a.cas_type,a.cas_user_id, a.username, a.realname, a.user_type, a.gender, a.birthday, a.tel_num,a.status, GROUP_CONCAT(c.role_flag) as roles from t_user_info a ,t_role c ,t_user_role b where a.id = b.user_id and b.role_id = c.id
and a.username=#{username}
and a.pwd=#{pwd}
